import './css/App.css';
import "react-multi-carousel/lib/styles.css"

import Section from './components/Section';
import StartPage from './components/StartPage';
import { useEffect } from 'react';
import ProjectCard from './components/ProjectCard';
import Carousel from 'react-multi-carousel'

function App() {
  const observer = new IntersectionObserver(entries => {
    entries.forEach(entry => {
      if (entry.isIntersecting) {
        console.log(entry);
        entry.target.classList.add(entry.target.dataset.animation);
      }
    });
  });
  
  useEffect(() => {
    console.log(document.querySelectorAll('.animate__animated'));
    document.querySelectorAll('.animate__animated').forEach(element => {
      observer.observe(element);
    });
  })

  return (
    <>
      <Section id="Start">
        <StartPage />
      </Section>
      <Section id="Biography">

      </Section>
      <Section id="Skills">

      </Section>
      <Section id="Experience">

      </Section>
      <Section id="Projects" secDesc={'A showcase of my favourite and most important projects'}>
        <Carousel
          arrows
          centerMode={false}
          containerClass="container-with-dots"
          draggable
          focusOnSelect={false}
          infinite
          itemClass=""
          keyBoardControl
          minimumTouchDrag={80}
          pauseOnHover
          renderArrowsWhenDisabled={false}
          renderButtonGroupOutside={false}
          renderDotsOutside={false}
          responsive={{
            desktop: {
              breakpoint: {
                max: 3000,
                min: 1024
              },
              items: 3,
              partialVisibilityGutter: 40
            },
            mobile: {
              breakpoint: {
                max: 464,
                min: 0
              },
              items: 1,
              partialVisibilityGutter: 30
            },
            tablet: {
              breakpoint: {
                max: 1024,
                min: 464
              },
              items: 2,
              partialVisibilityGutter: 30
            }
          }}
          rewind={false}
          rewindWithAnimation={false}
          rtl={false}
          shouldResetAutoplay
          showDots={false}
          sliderClass=""
          slidesToSlide={1}
          swipeable
        >
          <ProjectCard></ProjectCard>
          <ProjectCard></ProjectCard>
          <ProjectCard></ProjectCard>
        </Carousel>
      </Section>
      <Section id="Quotes">
        <div data-animation="animate__slideInLeft" className="animate__animated animate__faster">
            Heitor Silva
        </div>
      </Section>
    </>
  );
}

export default App;
