import './css/App.css';

import Section from './components/Section';
import StartPage from './components/StartPage';
import { useEffect } from 'react';
import ProjectCard from './components/ProjectCard';
import Carousel from './components/Carousel';

function App() {
    const observer = new IntersectionObserver(entries => {
    entries.forEach(entry => {
      if (entry.isIntersecting) {
        console.log(entry);
        entry.target.classList.add(entry.target.dataset.animation);
      }
    });
  });
  
  useEffect(() => {
    console.log(document.querySelectorAll('.animate__animated'));
    document.querySelectorAll('.animate__animated').forEach(element => {
      observer.observe(element);
    });
  })

  return (
    <>
      <Section id="Start">
        <StartPage />
      </Section>
      <Section id="Biography">

      </Section>
      <Section id="Skills">

      </Section>
      <Section id="Experience">

      </Section>
      <Section id="Projects" secDesc={'A showcase of my favourite and most important projects'}>
        <Carousel>
          <ProjectCard title='MoveStats' description='description still to be done' awards={['FEBRACE (Brazilian Science and Engineering Feir) 2021']} />
          <ProjectCard />
          <ProjectCard />
          <ProjectCard />
          <ProjectCard />
        </Carousel>
      </Section>
      <Section id="Quotes">
        <div data-animation="animate__slideInLeft" className="animate__animated animate__faster">
            Heitor Silva
        </div>
      </Section>
    </>
  );
}

export default App;
